import React, { useState, useEffect } from "react";
import { useHistory } from "react-router";
import * as yup from "yup";
import "./OrderForm.css";
import ofSchema from "../validation/orderFormSchema.js";

const initialFormValues = {
  firstName: "",
  lastName: "",
  email: "",
  streetAddress: "",
  city: "",
  state: "",
  zip: "",
  telephone: "",
};

const OrderForm = () => {
  const [formValues, setFormValues] = useState(initialFormValues);
  const [errors, setErrors] = useState(initialFormValues);
  const [disabled, setDisabled] = useState(true);

  useEffect(() => {
    ofSchema.isValid(formValues).then((valid) => {
      setDisabled(!valid);
    });
  }, [formValues]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    yup
      .reach(ofSchema, name)
      .validate(value)
      .then((valid) => {
        setErrors({ ...errors, [name]: "" });
      })
      .catch((err) => {
        setErrors({ ...errors, [name]: err.errors[0] });
      });
    setFormValues({
      ...formValues,
      [name]: value,
    });
  };

  const history = useHistory();

  // const handleChange = (e) => {
  //   setFormValues({ ...formValues, [e.target.name]: e.target.value });
  // };

  const handleSubmit = (e) => {
    e.preventDefault();
    history.push("/Confirmation");
  };

  return (
    <section id="order-section">
      <div className="order-div">
        <div className="coin">
          <div className="front"></div>
          <div className="front_b"></div>
          <div className="back"></div>
          <div className="back_b"></div>
        </div>
        <h3 className="order-text">Order Now!</h3>
        <form className="order-form" onSubmit={handleSubmit}>
          <label type="text">First name:</label>
          <input
            type="text"
            name="firstName"
            placeholder="first name"
            value={formValues.firstName}
            onChange={handleChange}
          />
          <label type="text">Last Name:</label>
          <input
            type="text"
            name="lastName"
            placeholder="last name"
            value={formValues.lastName}
            onChange={handleChange}
          />
          <label type="text">Email:</label>
          <input
            type="email"
            name="email"
            placeholder="email"
            value={formValues.email}
            onChange={handleChange}
          />
          <label type="text">Address:</label>
          <input
            type="text"
            name="streetAddress"
            placeholder="street address"
            value={formValues.streetAddress}
            onChange={handleChange}
          />
          <label type="text">City:</label>
          <input
            type="text"
            name="city"
            placeholder="city"
            value={formValues.city}
            onChange={handleChange}
          />
          <label type="text">State:</label>
          <input
            type="text"
            name="state"
            placeholder="state"
            value={formValues.state}
            onChange={handleChange}
          />
          <label type="text">Zip:</label>
          <input
            type="text"
            name="zip"
            placeholder="zip code"
            value={formValues.zip}
            onChange={handleChange}
          />
          <label type="text">Phone:</label>
          <input
            type="tel"
            name="telephone"
            placeholder="phone number"
            value={formValues.telephone}
            onChange={handleChange}
          />
          <button disabled={disabled} type="submit" onSubmit={handleSubmit}>
            Get Lucky!
          </button>
        </form>
      </div>
    </section>
  );
};

export default OrderForm;
