import React, { useState, useEffect } from "react";
import { useHistory } from "react-router";
import * as yup from "yup";
import "./OrderForm.css";
import ofSchema from "./orderFormSchema.js";

const initialFormValues = {
  name_first: "",
  name_last: "",
  email: "",
  street_address: "",
  city: "",
  state: "",
  zip_code: "",
  phone_number: "",
};

const OrderForm = () => {
  const [formValues, setFormValues] = useState(initialFormValues);
  const [errors, setErrors] = useState(initialFormValues);
  const [disabled, setDisabled] = useState("True");

  /* Each time the form value state is updated, check to see if it is valid per our schema.
  This will allow us to enable/disable the submit button.*/
  useEffect(() => {
    /* We pass the entire state into the entire schema, no need to use reach here.
    We want to make sure it is all valid before we allow a user to submit
    isValid comes from Yup directly */
    ofSchema.isValid(formValues).then((valid) => {
      setDisabled(!valid);
    });
  }, [formValues]);

  const history = useHistory();

  const handleChange = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    history.push("/Confirmation");
  };

  return (
    <section id="order-section">
      <div className="order-div">
        <div className="coin">
          <div className="front"></div>
          <div className="front_b"></div>
          <div className="back"></div>
          <div className="back_b"></div>
        </div>
        <h3 className="order-text">Order Now!</h3>
        <form className="order-form" onSubmit={handleSubmit}>
          <label type="text">First name:</label>
          <input
            type="text"
            name="firstName"
            placeholder="first name"
            value={formValues.name_first}
            onChange={handleChange}
          />
          <label type="text">Last Name:</label>
          <input
            type="text"
            name="lastName"
            placeholder="last name"
            value={formValues.name_last}
            onChange={handleChange}
          />
          <label type="text">Email:</label>
          <input
            type="email"
            name="email"
            placeholder="email"
            value={formValues.email}
            onChange={handleChange}
          />
          <label type="text">Address:</label>
          <input
            type="text"
            name="streetAddress"
            placeholder="street address"
            value={formValues.street_address}
            onChange={handleChange}
          />
          <label type="text">City:</label>
          <input
            type="text"
            name="city"
            placeholder="city"
            value={formValues.city}
            onChange={handleChange}
          />
          <label type="text">State:</label>
          <input
            type="text"
            name="state"
            placeholder="state"
            value={formValues.state}
            onChange={handleChange}
          />
          <label type="text">Zip:</label>
          <input
            type="text"
            name="zip"
            placeholder="zip code"
            value={formValues.zip_code}
            onChange={handleChange}
          />
          <label type="text">Phone:</label>
          <input
            type="tel"
            name="telephone"
            placeholder="phone number"
            value={formValues.phone_number}
            onChange={handleChange}
          />
          <button type="submit" onSubmit={handleSubmit}>
            Get Lucky!
          </button>
        </form>
      </div>
    </section>
  );
};

export default OrderForm;
